at timestamptz,\n  next_free_refill_at timestamptz,\n  last_paid_reset_at timestamptz,\n  next_paid_reset_at timestamptz,\n  updated_at timestamptz not null default timezone('utc', now())\n)","create or replace function public.touch_org_credit_balances()\nreturns trigger\nlanguage plpgsql\nas $$\nbegin\n  new.updated_at := timezone('utc', now());\n  return new;\nend;\n$$","create trigger org_credit_balances_updated_at\nbefore update on public.org_credit_balances\nfor each row execute function public.touch_org_credit_balances()","-- Credit transactions ledger\ncreate table if not exists public.credit_transactions (\n  id bigserial primary key,\n  org_id uuid not null references public.organizations(id) on delete cascade,\n  profile_id uuid references public.profiles(id),\n  request_id uuid references public.recipe_requests(id),\n  bucket text not null check (bucket in ('free','paid','bonus')),\n  change integer not null,\n  reason text not null default 'recipe_generation',\n  created_at timestamptz not null default timezone('utc', now())\n)","create index if not exists credit_transactions_org_idx on public.credit_transactions(org_id)","create index if not exists credit_transactions_request_idx on public.credit_transactions(request_id)","-- Keep plan_code constrained\nalter table public.subscriptions\n  add constraint subscriptions_plan_catalog_plan_code_fkey\n  foreign key (plan_code)\n  references public.plan_catalog(plan_code)","-- Utility to ensure balance row exists + align with plan\ncreate or replace function public.ensure_org_credit_balance(p_org_id uuid, p_plan_code text)\nreturns public.org_credit_balances\nlanguage plpgsql\nsecurity definer\nset search_path = public, auth\nas $$\ndeclare\n  v_plan text := coalesce(p_plan_code, 'free');\n  v_balance public.org_credit_balances;\n  v_plan_row public.plan_catalog%rowtype;\nbegin\n  select * into v_plan_row from public.plan_catalog where plan_code = v_plan;\n  if not found then\n    raise exception 'PLAN_NOT_FOUND';\n  end if;\n\n  insert into public.org_credit_balances (org_id, plan_code, free_credits_available, paid_credits_available, bonus_credits_available, last_free_refill_at, next_free_refill_at, last_paid_reset_at, next_paid_reset_at)\n  values (\n    p_org_id,\n    v_plan_row.plan_code,\n    v_plan_row.free_weekly_credits,\n    v_plan_row.included_paid_credits,\n    0,\n    timezone('utc', now()),\n    timezone('utc', now()) + interval '7 days',\n    timezone('utc', now()),\n    timezone('utc', now()) + interval '30 days'\n  )\n  on conflict (org_id) do update\n    set plan_code = coalesce(excluded.plan_code, public.org_credit_balances.plan_code)\n  returning * into v_balance;\n\n  return v_balance;\nend;\n$$","grant execute on function public.ensure_org_credit_balance(uuid, text) to authenticated, service_role","create or replace function public.spend_recipe_credit(\n  p_org_id uuid,\n  p_profile_id uuid,\n  p_request_id uuid default null,\n  p_reason text default 'recipe_generation'\n)\nreturns jsonb\nlanguage plpgsql\nsecurity definer\nset search_path = public, auth\nas $$\ndeclare\n  v_plan_code text;\n  v_plan public.plan_catalog%rowtype;\n  v_balance public.org_credit_balances%rowtype;\n  v_bucket text;\n  v_now timestamptz := timezone('utc', now());\nbegin\n  select plan_code into v_plan_code\n  from public.subscriptions\n  where org_id = p_org_id\n    and status in ('trialing','active')\n  order by created_at desc\n  limit 1;\n\n  select ensure_org_credit_balance(p_org_id, coalesce(v_plan_code, 'free')) into v_balance;\n\n  select * into v_plan from public.plan_catalog where plan_code = v_balance.plan_code;\n  if not found then\n    raise exception 'PLAN_NOT_FOUND';\n  end if;\n\n  select * into v_balance\n  from public.org_credit_balances\n  where org_id = p_org_id\n  for update;\n\n  if v_balance.next_free_refill_at is null or v_balance.next_free_refill_at <= v_now then\n    v_balance.free_credits_available := v_plan.free_weekly_credits;\n    v_balance.last_free_refill_at := v_now;\n    v_balance.next_free_refill_at := v_now + interval '7 days';\n  end if;\n\n  if v_balance.next_paid_reset_at is null or v_balance.next_paid_reset_at <= v_now then\n    v_balance.paid_credits_available := v_plan.included_paid_credits;\n    v_balance.last_paid_reset_at := v_now;\n    v_balance.next_paid_reset_at := v_now + interval '30 days';\n  end if;\n\n  if v_balance.free_credits_available > 0 then\n    v_balance.free_credits_available := v_balance.free_credits_available - 1;\n    v_bucket := 'free';\n  elsif v_balance.paid_credits_available > 0 then\n    v_balance.paid_credits_available := v_balance.paid_credits_available - 1;\n    v_bucket := 'paid';\n  elsif v_balance.bonus_credits_available > 0 then\n    v_balance.bonus_credits_available := v_balance.bonus_credits_available - 1;\n    v_bucket := 'bonus';\n  else\n    raise exception 'INSUFFICIENT_CREDITS';\n  end if;\n\n  update public.org_credit_balances\n  set\n    plan_code = v_plan.plan_code,\n    free_credits_available = v_balance.free_credits_available,\n    paid_credits_available = v_balance.paid_credits_available,\n    bonus_credits_available = v_balance.bonus_credits_available,\n    last_free_refill_at = v_balance.last_free_refill_at,\n    next_free_refill_at = v_balance.next_free_refill_at,\n    last_paid_reset_at = v_balance.last_paid_reset_at,\n    next_paid_reset_at = v_balance.next_paid_reset_at,\n    updated_at = v_now\n  where org_id = p_org_id;\n\n  insert into public.credit_transactions (\n    org_id,\n    profile_id,\n    request_id,\n    bucket,\n    change,\n    reason\n  ) values (\n    p_org_id,\n    p_profile_id,\n    p_request_id,\n    v_bucket,\n    -1,\n    coalesce(p_reason, 'recipe_generation')\n  );\n\n  return jsonb_build_object(\n    'bucket', v_bucket,\n    'free_remaining', v_balance.free_credits_available,\n    'paid_remaining', v_balance.paid_credits_available,\n    'bonus_remaining', v_balance.bonus_credits_available,\n    'next_free_refill_at', v_balance.next_free_refill_at,\n    'next_paid_reset_at', v_balance.next_paid_reset_at\n  );\nexception\n  when raise_exception then\n    if SQLERRM like '%INSUFFICIENT_CREDITS%' then\n      raise exception 'INSUFFICIENT_CREDITS';\n    else\n      raise;\n    end if;\nend;\n$$","grant execute on function public.spend_recipe_credit(uuid, uuid, uuid, text) to authenticated, service_role","-- RLS\nalter table public.plan_catalog enable row level security","alter table public.org_credit_balances enable row level security","alter table public.credit_transactions enable row level security","create policy \\"Plan catalog readable\\"\non public.plan_catalog\nfor select\nto authenticated\nusing (true)","create policy \\"Members view credit balances\\"\non public.org_credit_balances\nfor select\nusing (public.is_org_member(org_id))","create policy \\"Members view credit transactions\\"\non public.credit_transactions\nfor select\nusing (public.is_org_member(org_id))"}	plan_and_credits
