            active_sub.plan_code
           FROM active_sub
        UNION ALL
         SELECT o.id AS org_id,
            'free_v1'::text AS plan_code
           FROM public.organizations o
          WHERE (NOT (EXISTS ( SELECT 1
                   FROM active_sub a
                  WHERE (a.org_id = o.id))))
        ), base AS (
         SELECT s.org_id,
            pf.feature_code,
            pf.enabled,
            pf.caps
           FROM (sub s
             JOIN public.plan_features pf ON ((pf.plan_code = s.plan_code)))
        ), addons AS (
         SELECT oa.org_id,
